import React, {useState, useRef, useEffect} from "react";
import {Button} from "reactstrap";
import { gsap } from "gsap";


const QuestionBox = ({question, options, selected}) => {

    const boxRef = useRef();
    //const buttonRef = useRef();

    // wait until DOM has been rendered
    useEffect(() => {

    });

    function test1() {

        function test3() {
            gsap.to(boxRef.current, {opacity:0, duration:1, zIndex:-1});
        }

        function test2() {
            gsap.to(".questionBox:nth-child(4)", {opacity:1, duration:1, zIndex:999, onComplete:test3});
        }
        gsap.to(boxRef.current, {opacity:0, duration:1, onComplete:test2});
        console.log(boxRef.current);
        //gsap.to(".questionBox:nth-child(4)", {opacity:1, duration:1, zIndex:999});
        //console.log(boxRef.current);
        //gsap.to(boxRef.current, {opacity:0, duration:1, zIndex:-1});
        //console.log(boxRef.current);


    }

    const [answer, setAnswer] = useState(options);
    return (
        <div className="questionBox" ref={boxRef}>
            <div className="question"><h1> {question} </h1></div>
            {answer.map((text, index) => (

                <button key={index} 
                    onClick={() => {
                     setAnswer([text]);
                     selected(text);
                     test1();
                    }}
                    >
                    {text} 

                </button>
            ))}

        </div>

    )
}
export default QuestionBox;
