import React, {useState, useRef, useEffect} from "react";
import {Button} from "reactstrap";
import { gsap } from "gsap";


const QuestionBox = ({question, options, selected}) => {


    // wait until DOM has been rendered
    useEffect(() => {
        /*
        const divs = document.querySelectorAll(".questionBox:");
        gsap.set(divs[1], {x:100, opacity:1});

        gsap
        .timeline({repeat: -1, defaults:{duration:3}})
        .add("one")
        .to(divs[0], {y:})
        */
       // gsap.set(".questionBox:nth-child(5)", {opacity: 1});
     // gsap.to(".questionBox:nth-child(1)", { duration: 1, display:block, y: 0,ease: "power3.inOut"});
    });

    const [answer, setAnswer] = useState(options);
    return (
        <div className="questionBox">
            <div className="question"><h1> {question} </h1></div>
            {answer.map((text, index) => (

                <Button variant="outline-primary"
                    key={index} 
                    onClick={() => {
                     setAnswer([text]);
                     selected(text);
                     gsap
                     .timeline({repeat: -1, defaults:{duration:3}})
                     .add("one")
                     .to(".questionBox", {y:100, duration:1, ease: "power3.inOut"})
                    }}
                    >
                    {text} 

                </Button>
            ))}

        </div>

    )
}
export default QuestionBox;
